INSERT INTO `contacts` (`id`, `fullName`, `email`, `message`, `userId`, `createdAt`) VALUES
	(11, 'Nguyễn Văn A', 'vana@example.com', 'Hôm qua tôi đã đến nhà hàng của bạn và thực sự ấn tượng với những món ăn tuyệt vời. Mọi thứ đều rất ngon, từ món pasta hấp dẫn đến món sushi tươi ngon. Điều tuyệt vời nhất là phục vụ của nhân viên, họ rất thân thiện và nhiệt tình. Tôi nhất định sẽ quay lại để thưởng thức nhiều hơn. Xin cảm ơn!', NULL, '2023-10-09 17:45:11'),
	(12, 'Trần Thị B', 'thib@example.com', 'Mình rất hài lòng với chất lượng món steak ở đây. Nó được nấu vừa miệng và thực sự ngon. Không chỉ thế, không gian quán cũng rất thoải mái và ấm cúng. Sẽ giới thiệu nhà hàng này cho bạn bè của mình!', NULL, '2023-12-09 15:46:11'),
	(13, 'Lê Văn C', 'vanc@example.com', 'Tôi rất thích món hải sản ở đây, đặc biệt là dĩa hải sản. Mọi thứ đều tươi ngon và được chuẩn bị rất chất lượng. Một trải nghiệm ẩm thực tuyệt vời!', NULL, '2023-12-09 15:46:11'),
	(14, 'Phạm Thị D', 'phamd@example.com', 'Đồ dessert ở đây thật sự ngon miệng. Mỗi món đều tuyệt vời và làm từ những nguyên liệu tươi ngon. Mình rất hạnh phúc với trải nghiệm ẩm thực này.', NULL, '2023-12-09 15:46:11'),
	(15, 'Ngô Văn E', 'vane@example.com', 'Dịch vụ và không gian rất tuyệt vời! Nhân viên phục vụ rất tốt và không gian rất thoải mái. Sẽ quay lại!', NULL, '2023-12-09 15:46:11'),
	(16, 'Hoàng Thị F', 'thif@example.com', 'Sushi ở đây tươi ngon và thơm ngon. Đã có một trải nghiệm tuyệt vời với những món ăn tại nhà hàng của bạn.', NULL, '2023-12-09 15:46:11'),
	(17, 'Võ Văn G', 'vang@example.com', 'Mình rất thích các lựa chọn thực đơn chay ở đây. Chúng thực sự ngon và đa dạng. Một lựa chọn tốt cho người ăn chay!', NULL, '2023-12-09 15:46:11'),
	(18, 'Đặng Thị H', 'vophihoang252003@gmail.com', 'Thực đơn đồ uống tuyệt vời. Rất nhiều lựa chọn và mọi thứ đều ngon miệng.', NULL, '2023-12-09 15:46:11'),
	(19, 'Trần Văn I', 'vani@example.com', 'Mình đã thưởng thức những chiếc sườn nướng BBQ ở đây và chúng thực sự tuyệt vời. Nướng vừa đủ và gia vị rất ngon.', NULL, '2023-12-09 15:46:11'),
	(20, 'Nguyễn Thị K', 'thik@example.com', 'Pizza ở đây thật ngon và phong phú vị. Mình rất thích!', NULL, '2023-12-09 15:46:11'),
	(21, 'Lý Văn L', 'vanl@example.com', 'Nhân viên rất thân thiện và chăm sóc. Dịch vụ rất tốt.', NULL, '2023-12-09 15:46:11'),
	(22, 'Trần Thị M', 'thim@example.com', 'Mình rất thích những món ăn cay. Tại đây, chúng thực sự ngon và hấp dẫn.', NULL, '2023-12-09 15:46:11'),
	(24, 'Đinh Thị P', 'thip@example.com', 'Món khai vị thật sự ngon miệng. Đã có một trải nghiệm tuyệt vời ở đây!', NULL, '2023-12-09 15:46:11'),
	(58, 'Trần Đình C', 'vophihoang252003@gmail.com', 'Món ăn rất tuyệt vời', NULL, '2024-01-15 14:41:27'),
	(59, 'Phi Nhan', 'vophihoang252003@gmail.com', 'Quán phục vụ rất tốt, nhân viên nhiệt tình, thức ăn đa dạng', 13, '2024-01-20 15:16:27');
