INSERT INTO `reply_contacts` (`id`, `contactId`, `title`, `content`, `sendAt`) VALUES
	(1, 11, 'Cảm ơn bạn đã liên hệ với chúng tôi về trải nghiệm ở nhà hàng!', 'Chào bạn,\n\nChúng tôi rất vui khi nhận được liên hệ từ bạn về trải nghiệm tại nhà hàng. Chúng tôi luôn đánh giá cao mọi ý kiến đóng góp từ khách hàng. Điều này giúp chúng tôi cải thiện dịch vụ và mang đến trải nghiệm tốt hơn cho tất cả mọi người. Cảm ơn bạn rất nhiều và chúng tôi hy vọng sẽ được đón tiếp bạn một lần nữa.', '2023-12-10 14:27:26'),
	(2, 12, 'Hỗ trợ thông tin đặt chỗ và giờ hoạt động của nhà hàng', 'Xin chào,\n\nCảm ơn bạn đã yêu cầu thông tin về việc đặt chỗ và giờ hoạt động của nhà hàng. Chúng tôi xin gửi đến bạn thông tin cụ thể như sau: Nhà hàng mở cửa từ 10:00 sáng đến 10:00 tối mỗi ngày. Để đặt chỗ, bạn có thể liên hệ trực tiếp qua số điện thoại hoặc website của chúng tôi. Chúng tôi rất mong được đón tiếp bạn tại nhà hàng.', '2023-12-10 14:27:26'),
	(3, 13, 'Thông tin về dịch vụ giao hàng tại nhà hàng', 'Chào bạn,\n\nCảm ơn bạn đã quan tâm đến dịch vụ giao hàng của nhà hàng. Chúng tôi rất vui thông báo rằng chúng tôi có dịch vụ giao hàng tận nơi với nhiều món ăn đa dạng và ngon miệng. Bạn có thể đặt hàng qua ứng dụng di động của chúng tôi hoặc liên hệ trực tiếp để biết thêm thông tin chi tiết về dịch vụ này.', '2023-12-10 14:27:26'),
	(4, 14, 'Chương trình khuyến mãi dành cho khách hàng thân thiết', 'Xin chào,\n\nNhà hàng xin thông báo về chương trình khuyến mãi dành cho khách hàng thân thiết. Bạn sẽ được nhận ngay ưu đãi đặc biệt khi trở thành thành viên của chúng tôi. Cơ hội nhận quà tặng, giảm giá và các ưu đãi hấp dẫn khác đang chờ đón bạn. Đừng bỏ lỡ cơ hội trở thành một trong những khách hàng VIP của nhà hàng!', '2023-12-10 14:27:26'),
	(5, 15, 'Trả lời phản hồi về một đợt thử nghiệm món mới', 'Xin chào,\n\nChúng tôi rất cảm ơn phản hồi của bạn về món ăn mới trong thực đơn. Ý kiến của bạn vô cùng quý báu và sẽ giúp chúng tôi cải thiện món ăn hơn nữa. Chúng tôi cam kết tiếp thu ý kiến từ khách hàng và liên tục cải thiện để mang đến trải nghiệm tốt nhất cho mọi người.', '2023-12-10 14:27:26'),
	(6, 16, 'Giới thiệu các sự kiện đặc biệt tại nhà hàng', 'Xin chào bạn,\n\nChúng tôi rất vui được chia sẻ với bạn về các sự kiện đặc biệt tại nhà hàng trong thời gian tới. Chúng tôi sẽ tổ chức các buổi biểu diễn âm nhạc, các buổi hội thảo về ẩm thực và nhiều sự kiện thú vị khác. Hãy ghé thăm chúng tôi để tham gia cùng chúng tôi trong những sự kiện đặc biệt này!', '2023-12-10 14:27:26'),
	(7, 17, 'Đáp ứng yêu cầu đặc biệt của khách hàng', 'Xin chào,\n\nChúng tôi rất hân hạnh được đáp ứng yêu cầu đặc biệt của bạn. Chúng tôi luôn cố gắng hết mình để mang đến trải nghiệm tốt nhất cho khách hàng. Đừng ngần ngại liên hệ với chúng tôi nếu cần hỗ trợ thêm. Chúng tôi luôn sẵn sàng phục vụ bạn tốt nhất có thể.', '2023-12-10 14:27:26'),
	(8, 18, 'Trung Quốc công bố lập trường giải quyết xung đột Nga-Ukraine', '<p><u><em><strong>Trung Quốc c&ocirc;ng bố lập trường giải quyết xung đột Nga-Ukraine</strong></em></u></p>\n\n<div class="ddict_btn" style="left:391.444px; top:39px"><img src="chrome-extension://cianljdimgjlpmjllcbahmpdnicglaap/logo/48.png" /></div>\n', '2023-12-10 17:01:31'),
	(9, 24, 'Trung Quốc công bố lập trường giải quyết xung đột Nga-Ukraine', '<p><u><em><strong>Trung Quốc c&ocirc;ng bố lập trường giải quyết xung đột Nga-Ukraine</strong></em></u></p>\n\n<div class="ddict_div" style="left:15.2222px; max-width:371.444px; top:41.8889px"><img class="ddict_audio" src="chrome-extension://cianljdimgjlpmjllcbahmpdnicglaap/img/audio.png" />\n<p class="ddict_sentence">China announced its stance on resolving the Russia-Ukraine conflict</p>\n</div>\n', '2023-12-10 17:07:54'),
	(13, 58, 'Cảm ơn vì đã góp ý', '<p>Cảm ơn v&igrave; đ&atilde; g&oacute;p &yacute; của bạn</p>\n', '2024-01-15 14:42:24');
